import { React, useState } from "react";
import NoteModal from "../modals/NoteModal";
import ActiveNotes from "./ActiveNotes";
import ArchivedNotes from "./ArchivedNotes";

import {data} from "../api/data";

const Home =()=> {
    let dataInit = {
        title:'',
        content:''
    };
    const [noteDataList, setNoteDataList] = useState(data);
    const [home, setHome] = useState(true);
    const [showNoteModal, setShowNoteModal] = useState(false);
    const [edit, setEdit] = useState(false);
    const [noteData, setNoteData] = useState(dataInit);
    const [errors, setErrors] = useState({});

    const switchViews =()=> {
        setHome(!home);
    }

    const showModal =()=> {
        setShowNoteModal(true);
    }
    const hideNoteModal =()=> {
        setShowNoteModal(false);
    }

    //CRUD
    const addNote =()=> {
        setNoteDataList([...noteDataList, noteData]);
        hideNoteModal();
        setNoteData(dataInit);
    }
    const editNote =()=> {

    }
    const deleteNote =()=> {

    }

    //Errors
    const errorsExist =()=> {
        const { title, content } = noteData;
        const newErrors = {};
        if(!title || title == ''){
            newErrors.title = 'Title is required';
        }
        if(!content || content == ''){
            newErrors.content = 'Content is required';
        }
        return newErrors;
    }

    //Forms
    const handleChange =(property, value)=> {
        setNoteData({...noteData, [property]: value })
        if(errors[property]){
            setErrors({...errors, [property] : null})
        }
    }
    const handleSubmit =(e)=> {
        e.preventDefault();
        const newErrors = errorsExist();
        if(Object.keys(newErrors).length > 0){
            setErrors(newErrors);
        } else {
            addNote();
        }
    }


    return(
        <>
            {(home) ? <ActiveNotes data={noteDataList} switchViews={switchViews} showModal={showModal} editNote={editNote} deleteNote={deleteNote} /> : <ArchivedNotes data={noteDataList} switchViews={switchViews} editNote={editNote} deleteNote={deleteNote} /> }
            <NoteModal 
            show={showNoteModal} 
            onHide={hideNoteModal} 
            isEdit={edit} 
            noteData={noteData}
            handleChange={handleChange} 
            handleSubmit={handleSubmit}
            errors={errors} />
        </>
    );
}

export default Home;
