import { Button, Form, Modal } from "react-bootstrap";

const NoteModal =(props)=> {
    const {show, onHide, isEdit} = props;
    const addNote = props.addNote;

    return(
        <Modal
            show={show}
            onHide={onHide}
            centered={true}
            backdrop='static'
            keyboard={false}
        >
            <Modal.Header>
                <Modal.Title>{(isEdit)? 'Edit Note' : 'Create Note' }</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group>
                        <Form.Label>Title</Form.Label>
                        <Form.Control type="text" />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Content</Form.Label>
                        <Form.Text />
                    </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={onHide} variant='light' >Cancel</Button>
                <Button onClick={addNote} variant='light' >Save</Button>
            </Modal.Footer>
        </Modal>
    );
}

export default NoteModal;
