import React from "react";
import { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import CreateNote from "../components/CreateNote";
import ListNotes from "../components/ListNotes";
import NoteModal from "../modals/NoteModal";
import {data} from "../api/data";
import ActiveNotes from "./ActiveNotes";
import ArchivedNotes from "./ArchivedNotes";

const Home =()=> {
    const [datos, setDatos] = useState(data);
    const [home, setHome] = useState(true);
    const [showNoteModal, setShowNoteModal] = useState(false);
    const [editNote, setEditNote] = useState(false);


    const title = '';
    const content = '';

    const switchViews =()=> {
        setHome(!home);
    }

    const showModal =()=> {
        setShowNoteModal(true);
    }
    const hideNoteModal =()=> {
        setShowNoteModal(false);
    }

    const addNote =()=> {
        setDatos(...datos, {
            id: '10',
            title: title,
            content:content
        });
    }


    return(
        <>
            {(home) ? <ActiveNotes data={datos} switchViews={switchViews} showModal={showModal} /> : <ArchivedNotes data={datos} switchViews={switchViews} /> }
            <NoteModal show={showNoteModal} onHide={hideNoteModal} isEdit={editNote} addNote={addNote} />
        </>
    );
}

export default Home;
